Property.Value:
BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	SET NOCOUNT ON;

    IF NOT EXISTS(SELECT 1 FROM [dbo].[tb_user] WHERE LoginType = @LoginType and UserName = @UserName)
	BEGIN

		DECLARE @EmailVerification BIT = NULL;

		IF(@LoginType = 'Email')
		BEGIN
			SET @EmailVerification = 0;
		END

		INSERT INTO [dbo].[tb_user](LoginType, UserName, DisplayName, ProfilePictureURL, [Password], MobilePlatform, PushNotificationID, 
								    HMACHashKey, EmailVerification, Country, Region, City)
		SELECT @LoginType, @UserName, @Name, @ProfilePictureURL, @Password, @MobilePlatform, @PushNotificationID,
			   @HMACHashKey, @EmailVerification, @Country, @Region, @City;

		SELECT @ID = ID FROM [dbo].[tb_user] WHERE LoginType = @LoginType AND UserName = @UserName;

		INSERT INTO [dbo].[tb_user_onetimecode]([UserID], [Purpose])
		SELECT @ID, 'Account Activation'

		SET @Result = 'OK';

	END
	ELSE
	BEGIN
		set @Result = 'EXISTS';
	END


END

CREATE PROCEDURE [dbo].[usp_AddNewUser] 
(@LoginType VARCHAR(15), @UserName VARCHAR(50), @Name NVARCHAR(200), @ProfilePictureURL VARCHAR(200), @Password VARCHAR(50),
 @MobilePlatform VARCHAR(10), @PushNotificationID VARCHAR(200), @HMACHashKey VARCHAR(128),
 @Country VARCHAR(50), @Region VARCHAR(100), @City VARCHAR(100),
 @Result VARCHAR(200) OUTPUT, @ID BIGINT OUTPUT)
AS

BEGIN
	SET NOCOUNT ON;

    IF EXISTS(SELECT 1 FROM [dbo].[tb_used_nonce] WHERE LoginType = @LoginType AND UserName = @Username AND RequestTime = @RequestDate AND Nonce = @Nonce)
	BEGIN
		SET @Result = 'EXISTS';
	END
	ELSE
	BEGIN
		INSERT INTO [dbo].[tb_used_nonce](LoginType, UserName, RequestTime, Nonce)
		SELECT @LoginType, @Username, @RequestDate, @Nonce;

		SET @Result = 'OK';
	END
END

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
SELECT LoginType, UserName, A.ID, B.ActivationCode FROM [dbo].[tb_user] AS A INNER JOIN [dbo].[tb_user_onetimecode] AS B ON B.UserID = A.ID AND B.[Purpose] = 'Account Activation' WHERE LoginType = @LoginType AND UserName = @UserName AND [EmailVerification] = 0;
END

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
SELECT [LoginType] ,[UserName] ,[ID] ,[DisplayName] ,[ProfilePictureURL] ,[Password] ,[MobilePlatform] ,[PushNotificationID] ,[CreatedWhen] ,[TouchedWhen], [HMACHashKey] ,[Country], [Region], [City] FROM [dbo].[tb_user] WHERE LoginType = @LoginType and UserName = @UserName
END
